import "./../main.css"

export default function Node ({point}) {
    useState[classes, setClasses] = useState("");

    const setPoint = () => { 
        if(!point.isStart && !point.isEnd) {
            point.setStart(true);
        } else if (point.isStart) {
            point.setStart(false);
            point.setEnd(true);
        } else if (point.isEnd) {
            point.setStart(false);
            point.setEnd(false);
        }
    }

    return (
        <div className="node" onClick={setPoint}/>
    )
}
