import React, {useEffect, useState} from "react";
import {Point} from './../Objects/Point';
import Node from './Node';
import {Button} from 'react-bootstrap';
import './../main.css'

export default function Grid () {

    const [grid, setGrid] = useState([]);
    const [numSpecials, setNumSpecials] = useState(0);
    const [start, setStart] = useState(null);
    const [end, setEnd] = useState(null);


    useEffect(() => { 
        initGrid(30, 80);
    }, [])


    /**
     * Initializes an empty grid.
     * 
     * @param {*} rows # of rows
     * @param {*} cols # of columns.
     */
    const initGrid = (rows, cols) => {
        const tempGrid = [];

        for(let i = 0; i < rows; i++) {
            const currRow = [];
            for(let j = 0; j < cols; j++) {
                currRow.push(new Point(i, j));
            }   

            tempGrid.push(currRow);
        }

        setGrid(tempGrid);
    }

    return (
        <div className="container">
            <h1>Pathfinding Visualizer</h1>
            <Button>Breadth First Search</Button>
            <div>
                {grid.map((row, rowId) => {
                    return (
                        <div key={rowId} className="rowContainer">
                            {row.map((col, colId) => {
                                return (
                                    <Node key={colId} point={grid[rowId][colId]} numSpecials={numSpecials} setNumSpecials={setNumSpecials} setStart={setStart} setEnd={setEnd} />
                                )
                            })}
                        </div>
                    )
                    })}
            </div>
        </div>    
    )
}
